const posts=(state=[],action) => {
   switch (action.type) {
     case "FETCH_POSTS":
        return [...state,action.payload];
     case "CREATE_POST":
       console.log(action)
       return [...state,action.payload];
     default:
       return state;
   }
}
export default posts
