import Posts from "../Posts/Posts";

export default (state=[],action) => {
   switch (action.type) {
     case "UPDATE":
       return state.map((post)=>post._id===action._id?action.payload:post)
     case "FETCH_POSTS":
        return action.payload;
     case "CREATE_POST":
       console.log(action)
       return [...state,action.payload];
     default:
       return state;
   }
}
