import React,{useState,useEffect} from 'react'
import {Typography,AppBar, Toolbar, Avatar, Button} from '@material-ui/core'
import useStyles from './styles.js'
import memories from './memories.png'
import {Link,useHistory,useLocation } from 'react-router-dom'
import {useDispatch} from 'react-redux'


const Navbar = () => {
  const classes=useStyles()
  const dispatch=useDispatch()
  const history=useHistory()
  const location=useLocation()
  const [user,setUser]=useState(JSON.parse(localStorage.getItem('profile')))
 console.log(user)
  
  const logout = () => {
    dispatch({ type:"LOGOUT", });

    history.push('/auth');

    setUser(null);
  };
    
  useEffect(()=>{
     const token=user?.token

     setUser(JSON.parse(localStorage.getItem('profile')))
  },[location])
  return (
     <AppBar className={classes.appBar} position="static" color='inherit'>
       <div className={classes.brandContainer}>
         <Typography component={Link} to='/'  className={classes.heading}  variant='h2' align='center'> Memories</Typography>
         <img src={memories} className={classes.img} alt="memories" height="60"/>
        </div>
      <Toolbar className={classes.toolbar}>
        {user?.result?(
          <div className={classes.profile}>
            <Avatar className={classes.purple} alt={user?.result.name} src={user?.result.imageUrl}>{user?.result.name.charAt(0)}</Avatar>
            <Typography className={classes.userName} variant='h6'>{user?.result.name}</Typography>
            <Button variant='contained' color='secondary' className={classes.logout} onClick={logout}>logout</Button>
          </div>
        ):(
          <Button component={Link} to="/auth" variant='contained' color="primary"  >Sign In</Button>
        )}
      </Toolbar>
      </AppBar> 
  )
}

export default Navbar
