import React,{useState} from 'react'
import {Typography,AppBar, Toolbar, Avatar, Button} from '@material-ui/core'
import useStyles from './styles.js'
import memories from './memories.png'
import {Link } from 'react-router-dom'


const Navbar = () => {
  const classes=useStyles()
  const [user,setUser]=useState(JSON.parse(localStorage.getItem('profile')))
  console.log(user)
  return (
     <AppBar className={classes.appBar} position="static" color='inherit'>
       <div className={classes.brandContainer}>
         <Typography component={Link} to='/'  className={classes.heading}  variant='h2' align='center'> Memories</Typography>
         <img src={memories} className={classes.img} alt="memories" height="60"/>
        </div>
      <Toolbar className={classes.toolbar}>
        {user?(
          <div className={classes.profile}>
            <Avatar className={classes.purple} alt={user.profile.name} src={user.profile.imageUrl}>{user.profile.name.charAt(0)}</Avatar>
            <Typography className={classes.userName} variant='h6'>{user.profile.name}</Typography>
            <Button variant='contained' color='secondary' className={classes.logout}>logout</Button>
          </div>
        ):(
          <Button component={Link} to="/auth" variant='contained' color="primary"  >Sign In</Button>
        )}
      </Toolbar>
      </AppBar> 
  )
}

export default Navbar
