export default (state=[],action) => {
   switch (action.type) {
     case "UPDATE":
       return state.posts.map((post)=>post._id===action.payload?action.payload:post)
     case "FETCH_POSTS":
        return action.payload;
     case "CREATE_POST":
       console.log(action)
       return [...state,action.payload];
     default:
       return state;
   }
}
